import React, { useState } from 'react';
import Carousel from 'react-multi-carousel';
import 'react-multi-carousel/lib/styles.css';
import meter1 from "../assets/img/Nathan-Team22.png";
import meter2 from "../assets/img/Philippe-Team.png";
import meter3 from "../assets/img/Killian-Team.png";
import meter4 from "../assets/img/Maxime-Team.png";
import meter5 from "../assets/img/Isaak-Team.png";
import meter6 from "../assets/img/Clement-Team.png";
import meter7 from "../assets/img/Quentin-Team.png";

export const Skills = () => {
  const [currentSlide, setCurrentSlide] = useState(0);

  const handleSlideChange = (nextSlide) => {
    console.log("Current Slide:", nextSlide);

    if (nextSlide > 7) {
      nextSlide = 0;
    }
    setCurrentSlide(nextSlide);
  };

  const responsive = {
    superLargeDesktop: {
      breakpoint: { max: 4000, min: 3000 },
      items: 5
    },
    desktop: {
      breakpoint: { max: 3000, min: 1024 },
      items: 3
    },
    tablet: {
      breakpoint: { max: 1024, min: 464 },
      items: 2
    },
    mobile: {
      breakpoint: { max: 464, min: 0 },
      items: 1
    }
  };

  const renderText = (currentSlide) => {
    switch (currentSlide) {
      case 0:
        return "Nathan SABOURDY";
      case 1:
        return "Philippe CHEYPE";
      case 2:
        return "Killian DESSERICH";
      case 3:
        return "Maxime ROTH-FESSLER";
      case 4:
        return "Isaak NICOLAS";
      case 5:
        return "Clément BERNAT";
      case 6:
        return "Quentin ROUVIER";
      default:
        return ""; // Add default case to handle unexpected values
    }
  };

  return (
    <section className="skill" id="team">
      <div className="container">
        <div className="row">
          <div className="col-12">
            <div className="skill-bx wow zoomIn">
              <h5 style={{ color: 'black' }}>{renderText(currentSlide)}</h5> {/* Text updated based on currentSlide */}
              <h2>Equipe</h2>
              <p>
                Lorem Ipsum is simply dummy text of the printing and typesetting industry.
                <br />
                Lorem Ipsum has been the industry's standard dummy text.
              </p>
              <Carousel
                responsive={responsive}
                infinite={true}
                className="skill-slider"
                beforeChange={handleSlideChange}
              >
                <div className="item">
                  <img src={meter1} alt="Image1" />
                </div>
                <div className="item">
                  <img src={meter2} alt="Image2" />
                </div>
                <div className="item">
                  <img src={meter3} alt="Image3" />
                </div>
                <div className="item">
                  <img src={meter4} alt="Image4" />
                </div>
                <div className="item">
                  <img src={meter5} alt="Image5" />
                </div>
                <div className="item">
                  <img src={meter6} alt="Image6" />
                </div>
                <div className="item">
                  <img src={meter7} alt="Image" />
                </div>
              </Carousel>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};
