import React, { useState, useRef } from 'react';

import meter1 from "../assets/img/Nathan-Team22.png";
import meter2 from "../assets/img/Philippe-Team.png";
import meter3 from "../assets/img/Killian-Team.png";
import meter4 from "../assets/img/Maxime-Team.png";
import meter5 from "../assets/img/Isaak-Team.png";
import meter6 from "../assets/img/Clement-Team.png";
import meter7 from "../assets/img/Quentin-Team.png";
import Carousel from 'react-multi-carousel';
import 'react-multi-carousel/lib/styles.css';
import arrow1 from "../assets/img/arrow1.svg";
import arrow2 from "../assets/img/arrow2.svg";
import colorSharp from "../assets/img/color-sharp.png";
import colorSharpR from "../assets/img/color-sharp-right.png";

export const Skills = () => {
  const [slideIndex, setSlideIndex] = useState(0);

  const responsive = {
    superLargeDesktop: {
      // the naming can be any, depends on you.
      breakpoint: { max: 4000, min: 3000 },
      items: 5
    },
    desktop: {
      breakpoint: { max: 3000, min: 1024 },
      items: 3
    },
    tablet: {
      breakpoint: { max: 1024, min: 464 },
      items: 2
    },
    mobile: {
      breakpoint: { max: 464, min: 0 },
      items: 1
    }
  };

  const carouselRef = useRef();

  // Function to handle going to the previous slide
  const goToPrevSlide = () => {
    carouselRef.current.previous();
    console.log("slide Index --> %d", slideIndex)
    setSlideIndex(slideIndex + 1);
  };

  // Function to handle going to the next slide
  const goToNextSlide = () => {
    carouselRef.current.next();
    console.log("slide Index --> %d", slideIndex)
    setSlideIndex(slideIndex - 1);
  };

  const handleNextClick = () => {
    console.log("slide Index --> %d", slideIndex)
    setSlideIndex(slideIndex + 1);
  };

  const handlePrevClick = () => {
    console.log("slide Index --> %d", slideIndex)
    setSlideIndex(slideIndex - 1);
  };

  const handleButtonClick = (label) => {
    if (label === "Go to previous slide") {
      handlePrevClick();
    } else {
      handleNextClick();
    }
  };

  return (
    <section className="skill" id="team">
      <div className="container">
        <div className="row">
          <div className="col-12">
            <div className="skill-bx wow zoomIn">
              <h2>Equipe</h2>
              <p>Lorem Ipsum is simply dummy text of the printing and typesetting industry.<br></br> Lorem Ipsum has been the industry's standard dummy text.</p>
              <MyCarousel/>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};


const MyCarousel = () => {
  const carouselRef = useRef();

  // Function to handle going to the previous slide
  const goToPrevSlide = () => {
    carouselRef.current.previous();
  };

  // Function to handle going to the next slide
  const goToNextSlide = () => {
    carouselRef.current.next();
  };

  return (
    <div>
      <Carousel
        ref={carouselRef}
        infinite={true}
        className="owl-carousel owl-theme skill-slider"
      >
        {/* Carousel items */}
      </Carousel>
      {/* Previous button */}
      <button
        aria-label="Go to previous slide"
        className="react-multiple-carousel__arrow react-multiple-carousel__arrow--left"
        type="button"
        onClick={goToPrevSlide}
      ></button>
      {/* Next button */}
      <button
        aria-label="Go to next slide"
        className="react-multiple-carousel__arrow react-multiple-carousel__arrow--right"
        type="button"
        onClick={goToNextSlide}
      ></button>
    </div>
  );
};
