import React from 'react';
import Carousel from 'react-multi-carousel';
import 'react-multi-carousel/lib/styles.css';
import meter1 from "../assets/img/team/Nathan-Team22.png";
import meter2 from "../assets/img/team/Philippe-Team.png";
import meter3 from "../assets/img/team/Killian-Team.png";
import meter4 from "../assets/img/team/Maxime-Team.png";
import meter5 from "../assets/img/team/Isaak-Team.png";
import meter6 from "../assets/img/team/Clement-Team.png";
import meter7 from "../assets/img/team/Quentin-Team.png";

export const Skills = () => {
  const meterImages = [
    { src: meter1, alt: 'Image', title: 'Web Development', description: "Description 1" },
    { src: meter2, alt: 'Image', title: 'Brand Identity', description: "Description 2" },
    { src: meter3, alt: 'Image', title: 'Logo Design', description: "Description 3" },
    { src: meter4, alt: 'Image', title: 'Web Development', description: "Description 4" },
    { src: meter5, alt: 'Image', title: 'Web Development', description: "Description 5" },
    { src: meter6, alt: 'Image', title: 'Web Development', description: "Description 6" },
    { src: meter7, alt: 'Image', title: 'Web Development', description: "Description 7" },
  ];

  const responsive = {
    superLargeDesktop: {
      breakpoint: { max: 4000, min: 3000 },
      items: 1
    },
    desktop: {
      breakpoint: { max: 3000, min: 1024 },
      items: 1
    },
    tablet: {
      breakpoint: { max: 1024, min: 464 },
      items: 1
    },
    mobile: {
      breakpoint: { max: 464, min: 0 },
      items: 1
    }
  };

  return (
    <section className="skill" id="team">
      <div className="container">
        <div className="row">
          <div className="col-12">
            <div className="CarousselP-bx wow zoomIn" >
              <h2>Equipe</h2>
              <Carousel responsive={responsive} className="skill-slider" infinite={true}>
                {meterImages.map((image, index) => {
                  const prevIndex = (index - 1 + meterImages.length) % meterImages.length;
                  const nextIndex = (index + 1) % meterImages.length;
                  const prevImage = meterImages[prevIndex];
                  const nextImage = meterImages[nextIndex];
                  return (
                    <div className="item row" key={index}>
                      <div className="col-4">
                        <h3 style={{ color: "white" }}>{prevImage.description}</h3>
                        <h5 style={{ color: "white" }}>{prevImage.title}</h5>
                        <img src={prevImage.src} alt={prevImage.alt} />
                      </div>
                      <div className="col-4">
                        <h3 style={{ color: "#64a9ea" }}>{image.description}</h3>
                        <h5>{image.title}</h5>
                        <img src={image.src} alt={image.alt} />
                      </div>
                      <div className="col-4">
                        <h3 style={{ color: "white" }}>{nextImage.description}</h3>
                        <h5 style={{ color: "white" }}>{nextImage.title}</h5>
                        <img src={nextImage.src} alt={nextImage.alt} />
                      </div>
                    </div>
                  );
                })}
              </Carousel>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};
