import React, { useState } from 'react';
import meter1 from "../assets/img/Nathan-Team22.png";
import meter2 from "../assets/img/Philippe-Team.png";
import meter3 from "../assets/img/Killian-Team.png";
import meter4 from "../assets/img/Maxime-Team.png";
import meter5 from "../assets/img/Isaak-Team.png";
import meter6 from "../assets/img/Clement-Team.png";
import meter7 from "../assets/img/Quentin-Team.png";
import Carousel from 'react-multi-carousel';
import 'react-multi-carousel/lib/styles.css';
import arrow1 from "../assets/img/arrow1.svg";
import arrow2 from "../assets/img/arrow2.svg";
import colorSharp from "../assets/img/color-sharp.png"
import colorSharpR from "../assets/img/color-sharp-right.png"

export const Skills = () => {
  const [currentSlide, setCurrentSlide] = useState(0);

  const handleSlideChange = (nextSlide) => {
    setCurrentSlide(nextSlide);
  };

  const responsive = {
    superLargeDesktop: {
      breakpoint: { max: 4000, min: 3000 },
      items: 5
    },
    desktop: {
      breakpoint: { max: 3000, min: 1024 },
      items: 3
    },
    tablet: {
      breakpoint: { max: 1024, min: 464 },
      items: 2
    },
    mobile: {
      breakpoint: { max: 464, min: 0 },
      items: 1
    }
  };

  const renderText = () => {
    switch (currentSlide) {
      case 0:
        return "Web Development";
      case 1:
        return "Brand Identity";
      case 2:
        return "Logo Design";
      case 3:
        return "Web Development";
      case 4:
        return "Web Development";
      case 5:
        return "Web Development";
      case 6:
        return "Web Development";
      default:
        return "";
    }
  };

  return (
    <section className="skill" id="team">
      <div className="container">
        <div className="row">
          <div className="col-12">
            <div className="skill-bx wow zoomIn">
              <h5  style={{ color: 'black' }}>{renderText()}</h5> {/* Text rendered above the Carousel */}
              <h2>Equipe</h2>
              <p>
                Lorem Ipsum is simply dummy text of the printing and typesetting industry.
                <br />
                Lorem Ipsum has been the industry's standard dummy text.
              </p>
              <Carousel
                responsive={responsive}
                infinite={true}
                className="owl-carousel owl-theme skill-slider"
                beforeChange={handleSlideChange}
              >
                <div className="item">
                  <img src={meter1} alt="Image" />
                </div>
                <div className="item">
                  <img src={meter2} alt="Image" />
                </div>
                <div className="item">
                  <img src={meter3} alt="Image" />
                </div>
                <div className="item">
                  <img src={meter4} alt="Image" />
                </div>
                <div className="item">
                  <img src={meter5} alt="Image" />
                </div>
                <div className="item">
                  <img src={meter6} alt="Image" />
                </div>
                <div className="item">
                  <img src={meter7} alt="Image" />
                </div>
              </Carousel>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};
