import React, { useState } from 'react';
import meter1 from "../assets/img/Nathan-Team22.png";
import meter2 from "../assets/img/Philippe-Team.png";
import meter3 from "../assets/img/Killian-Team.png";
import meter4 from "../assets/img/Maxime-Team.png";
import meter5 from "../assets/img/Isaak-Team.png";
import meter6 from "../assets/img/Clement-Team.png";
import meter7 from "../assets/img/Quentin-Team.png";
import Carousel from 'react-multi-carousel';
import 'react-multi-carousel/lib/styles.css';
import arrow1 from "../assets/img/arrow1.svg";
import arrow2 from "../assets/img/arrow2.svg";
import colorSharp from "../assets/img/color-sharp.png"
import colorSharpR from "../assets/img/color-sharp-right.png"

export const Skills = () => {
  const [currentSlide, setCurrentSlide] = useState(0);

  const handleSlideChange = (nextSlide) => {
    setCurrentSlide(nextSlide);
  };

  const images = [
    { id: 'meter1', text: 'Web Development' },
    { id: 'meter2', text: 'Brand Identity' },
    { id: 'meter3', text: 'Logo Design' },
    { id: 'meter4', text: 'Web Development' },
    { id: 'meter5', text: 'Web Development' },
    { id: 'meter6', text: 'Web Development' },
    { id: 'meter7', text: 'Web Development' },
  ];

  const responsive = {
    superLargeDesktop: {
      breakpoint: { max: 4000, min: 3000 },
      items: 5
    },
    desktop: {
      breakpoint: { max: 3000, min: 1024 },
      items: 3
    },
    tablet: {
      breakpoint: { max: 1024, min: 464 },
      items: 2
    },
    mobile: {
      breakpoint: { max: 464, min: 0 },
      items: 1
    }
  };

  const renderText = () => {
    return images[currentSlide]?.text || ''; // Using optional chaining to handle undefined index
  };

  return (
    <section className="skill" id="team">
      <div className="container">
        <div className="row">
          <div className="col-12">
            <div className="skill-bx wow zoomIn">
              <h5>{renderText()}</h5> {/* Text updated based on currentSlide */}
              <h2>Equipe</h2>
              <p>
                Lorem Ipsum is simply dummy text of the printing and typesetting industry.
                <br />
                Lorem Ipsum has been the industry's standard dummy text.
              </p>
              <Carousel
                responsive={responsive}
                infinite={true}
                className="owl-carousel owl-theme skill-slider"
                beforeChange={handleSlideChange}
              >
                {images.map((image, index) => (
                  <div key={index} className="item">
                    <img src={eval(image.id)} alt="Image" />
                  </div>
                ))}
              </Carousel>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};
