import React, { useState, useMemo } from 'react';
import { Container, Row, Col, Tab, Nav } from 'react-bootstrap';
import Carousel from 'react-multi-carousel';
import 'react-multi-carousel/lib/styles.css';
import TrackVisibility from 'react-on-screen';


// Mobile Pictures Img
import login from '../assets/img/mobile/login.png';
import subscribe from '../assets/img/mobile/subscribe.png';
import homepage from '../assets/img/mobile/homepage.png';
import profile from '../assets/img/mobile/profile.png';
import sensorsAdd from '../assets/img/mobile/sensorsAdd.png';
import sensorStatWithoutGlobalConsumption from '../assets/img/mobile/sensorStatsWithoutGlobalConsumption.png';
import sensorStatWithGlobalConsumption from '../assets/img/mobile/sensorStatWithGlobalConsumption.png';
import stats from '../assets/img/mobile/stats.png';
// END Mobile Pictures Img

import meter1 from '../assets/img/team/Philippe-Team.png';
import meter2 from '../assets/img/team/Nathan-Team22.png';
import meter3 from '../assets/img/team/Killian-Team.png';
import meter4 from '../assets/img/team/Maxime-Team.png';
import meter5 from '../assets/img/team/Isaak-Team.png';
import meter6 from '../assets/img/team/Clement-Team.png';
import meter7 from '../assets/img/team/Quentin-Team.png';


export const Projects = () => {

  const mobileImages = useMemo(
    () => [
      { src: login, alt: 'Image', title: 'Connexion', description:"La page de connexion vous permettra de vous connecter à votre application Tidaly."},
      { src: subscribe, alt: 'Image', title: 'Inscription', description:"La page d’inscription vous permettra de créer un compte chez Tidaly. Votre page d’accueil vous aidera rapidement à prendre connaissance de votre consommation du jour mais également à prendre connaissance de votre consommation sur la semaine et sur le mois actuel." },
      { src: homepage, alt: 'Image', title: 'Accueil', description:"La page statistiques vous donnera les informations nécessaires pour une analyse complète de votre consommation d’eau en litres et en euros dans le temps." },
      { src: stats, alt: 'Image', title: 'Statistiques', description:"La page capteurs vous donnera la possibilité d’enregistrer vos matériels Tidaly Hub connectés ou bien Capteur(s) par point d’eau. Vous aurez également la possibilité de prendre connaissance de la consommation actuelle de votre Hub et de vos capteurs par point d’eau." },
      { src: sensorsAdd, alt: 'Image', title: 'Mes capteurs', description:"zizi" },
      { src: sensorStatWithGlobalConsumption, alt: 'Image', title: 'Statistiques de consommation des capteurs sur la consommation globale', description:"zizi" },
      { src: sensorStatWithoutGlobalConsumption, alt: 'Image', title: 'Statistiques de consommation globale des capteurs', description:"zizi" },
      { src: profile, alt: 'Image', title: 'Profil utilisateur', description:"zizi" },
    ],
    []
  );

  const webImages = useMemo(
    () => [
      { src: meter1, alt: 'Image', title: 'Web Developmentfeozfpoe', description:"zizi" },
      { src: meter2, alt: 'Image', title: 'Brand Identity' , description:"zizi"},
      { src: meter3, alt: 'Image', title: 'Logo Design' , description:"zizi"},
      { src: meter4, alt: 'Image', title: 'Web Development' , description:"zizi"},
      { src: meter5, alt: 'Image', title: 'Web Development' , description:"zizi"},
      { src: meter6, alt: 'Image', title: 'Web Development' , description:"zizi"},
      { src: meter7, alt: 'Image', title: 'Web Development' , description:"zizi"},
    ],
    []
  );

  const sensorsImages = useMemo(
    () => [
      { src: meter1, alt: 'Image', title: 'Web DevelopmentiXX', description:"zizi"},
      { src: meter2, alt: 'Image', title: 'Brand Identity', description:"zizi" },
      { src: meter3, alt: 'Image', title: 'Logo Design', description:"zizi" },
      { src: meter4, alt: 'Image', title: 'Web Development', description:"zizi" },
      { src: meter5, alt: 'Image', title: 'Web Development', description:"zizi" },
      { src: meter6, alt: 'Image', title: 'Web Development', description:"zizi" },
      { src: meter7, alt: 'Image', title: 'Web Development', description:"zizi" },
    ],
    []
  );

  const responsive = {
    superLargeDesktop: {
      // the naming can be any, depends on you.
      breakpoint: { max: 4000, min: 3000 },
      items: 1
    },
    desktop: {
      breakpoint: { max: 3000, min: 1024 },
      items: 1
    },
    tablet: {
      breakpoint: { max: 1024, min: 464 },
      items: 1
    },
    mobile: {
      breakpoint: { max: 464, min: 0 },
      items: 1
    }
  };

  const [activeTab, setActiveTab] = useState('first');

  const handleTabChange = (tab) => {
    setActiveTab(tab);
  };

  const MemoizedCarousel = React.memo(({ images }) => (
    <Carousel responsive={responsive} className="projectS-slider" infinite={true} showDots={true}>
      {images.map((image, index) => (
        <div className="item" key={index}>
          <h2 style={{ color: "black" }}>{image.title}</h2>
          <p style={{ color: "black" }}>{image.description}</p>
          <img src={image.src} alt={image.alt}  />
          <p style={{ color: "white" }}>space</p>
        </div>
      ))}
    </Carousel>
  ));

  return (
    <section className="project" id="projects">
      <Container>
        <Row>
          <Col>
            <TrackVisibility>
              {({ isVisible }) => (
                <div className={isVisible ? 'animate__animated animate__fadeIn' : ''}>
                  <h2>Projet</h2>
                  <p style={{ color: 'white' }}>
                      <b>
                      Tidaly est une application web et mobile pour surveiller votre consommation d'eau en temps réel.

                      Suivez votre consommation sur chaque point d'eau, chez vous ou bien en déplacement.

                      Avec l'analyse de données, les alertes et le coaching personnalisé, économisez de l'eau et de l'argent en
                      comprenant votre consommation.
                      </b>
                  </p>
                  <Tab.Container id="projects-tabs" activeKey={activeTab} onSelect={handleTabChange}>
                    <Nav
                      variant="pills"
                      className="nav-pills mb-5 justify-content-center align-items-center"
                      id="pills-tab"
                    >
                      <Nav.Item>
                        <Nav.Link eventKey="first">
                          <b>Web</b>
                        </Nav.Link>
                      </Nav.Item>
                      <Nav.Item>
                        <Nav.Link eventKey="second">
                          <b>Mobile</b>
                        </Nav.Link>
                      </Nav.Item>
                      <Nav.Item>
                        <Nav.Link eventKey="third">
                          <b>Matériel</b>
                        </Nav.Link>
                      </Nav.Item>
                    </Nav>
                    <Tab.Content>
                      <Tab.Pane eventKey="first">
                        <Row>
                          <Col>
                            <div className="CarousselP-bx wow zoomIn">
                              <MemoizedCarousel images={webImages} />
                            </div>
                          </Col>
                        </Row>
                      </Tab.Pane>
                      <Tab.Pane eventKey="second">
                        <Row>
                          <Col>
                            <div className="CarousselP-bx wow zoomIn">
                              <MemoizedCarousel images={mobileImages} />
                            </div>
                          </Col>
                        </Row>
                      </Tab.Pane>
                      <Tab.Pane eventKey="third">
                        <Row>
                          <Col>
                            <div className="CarousselP-bx wow zoomIn">
                              <MemoizedCarousel images={sensorsImages} />
                            </div>
                          </Col>
                        </Row>
                      </Tab.Pane>
                    </Tab.Content>
                  </Tab.Container>
                </div>
              )}
            </TrackVisibility>
          </Col>
        </Row>
      </Container>
      <h1 style={{ color: '#64a9ea' }}>d</h1>
    </section>

  );
};
