import React, { useState, useEffect, useMemo } from 'react';
import meter2 from "../assets/img/team/Nathan-Team22.png";
import meter1 from "../assets/img/team/Philippe-Team.png";
import meter3 from "../assets/img/team/Killian-Team.png";
import meter4 from "../assets/img/team/Maxime-Team.png";
import meter5 from "../assets/img/team/Isaak-Team.png";
import meter6 from "../assets/img/team/Clement-Team.png";
import meter7 from "../assets/img/team/Quentin-Team.png";
import Carousel from 'react-multi-carousel';
import 'react-multi-carousel/lib/styles.css';

export const Skills = () => {

  const MemoizedCarousel = React.memo(({ images }) => (
    <Carousel responsive={responsive} className="skill-slider" infinite={true}>
      {images.map((image, index) => {
        // Calculate previous and next indexes
        const prevIndex = (index - 1 + images.length) % images.length;
        const nextIndex = (index + 1) % images.length;

        // Get previous and next images
        const prevImage = images[prevIndex];
        const nextImage = images[nextIndex];

        return (
          <div className="item row" key={index}>
            {/* Previous Image */}
            <div className="col-4">
              <h3 style={{ color: "#64a9ea" }}>{prevImage.description}</h3>
              <h5>{prevImage.title}</h5>
              <img src={prevImage.src} alt={prevImage.alt} />
            </div>

            {/* Current Image */}
            <div className="col-4">

              <img src={image.src} alt={image.alt} />
            </div>

            {/* Next Image */}
            <div className="col-4">
              <h3 style={{ color: "#64a9ea" }}>{nextImage.description}</h3>
              <h5>{nextImage.title}</h5>
              <img src={nextImage.src} alt={nextImage.alt} />
            </div>
          </div>
        );
      })}
    </Carousel>
  ));



  const meterImages = useMemo(
    () => [
      { src: meter1, alt: 'Image', title: 'Web Development', description:"zizi"},
      { src: meter2, alt: 'Image', title: 'Brand Identity', description:"zizi" },
      { src: meter3, alt: 'Image', title: 'Logo Design', description:"zizi" },
      { src: meter4, alt: 'Image', title: 'Web Development', description:"zizi" },
      { src: meter5, alt: 'Image', title: 'Web Development', description:"zizi" },
      { src: meter6, alt: 'Image', title: 'Web Development', description:"zizi" },
      { src: meter7, alt: 'Image', title: 'Web Development', description:"zizi" },
    ],
    []
  );

  const responsive = {
    superLargeDesktop: {
      // the naming can be any, depends on you.
      breakpoint: { max: 4000, min: 3000 },
      items: 1
    },
    desktop: {
      breakpoint: { max: 3000, min: 1024 },
      items: 1
    },
    tablet: {
      breakpoint: { max: 1024, min: 464 },
      items: 1
    },
    mobile: {
      breakpoint: { max: 464, min: 0 },
      items: 1
    }
  };

  return (
    <section className="skill" id="team">
      <div className="container">
        <div className="row">
          <div className="col-12">
            <div className="CarousselP-bx wow zoomIn" >
              <h2>Equipe</h2>
                <MemoizedCarousel images={meterImages} />
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};
