import React, { useEffect } from 'react';
import { Container, Row, Col, Tab, Nav } from 'react-bootstrap';
import { Chrono } from "react-chrono";

const Home = () => {
  const items = [{
    title: "IDÉATION",
    url: "http://www.history.com",
    cardSubtitle:"Men of the British Expeditionary Force (BEF) wade out to..",
    cardDetailedText: "Men of the British Expeditionary Force (BEF) wade out to..",
    media: {
      type: "IMAGE",
      source: {
        url: "http://someurl/image.jpg"
      }
    }
  },

  {
    title: "HACKING INDUSTRY CAMP",
    cardSubtitle: "Event Subtitle",
    cardDetailedText: "Detailed description of the event.",
  },

  {
    title: "TESTE ET APPRENTISSAGE",
    cardTitle: "Event Title",
    cardSubtitle: "Event Subtitle",
    cardDetailedText: "Detailed description of the event.",
  },

  {
    title: "MANAGEMENT ET PROCESSUS",
    cardTitle: "Event Title",
    cardSubtitle: "Event Subtitle",
    cardDetailedText: "Detailed description of the event.",
  },

  {
    title: "ALPHA PROTOTYPE",
    cardTitle: "Event Title",
    cardSubtitle: "Event Subtitle",
    cardDetailedText: "Detailed description of the event.",
  },

  {
    title: "BETA PROTOTYPE",
    cardTitle: "Event Title",
    cardSubtitle: "Event Subtitle",
    cardDetailedText: "Detailed description of the event.",
  },

];

  return (
    <div style={{ width: "100%", height: "80%", marginBottom: "-80px"}}>
        <Chrono items={items}
            hideControls={false}
            mode="HORIZONTAL"
            slideShow
            scrollable
            theme={{
                primary:  "#FFF",
                secondary: "#FFF",
                cardBgColor: "#FFF",
                cardForeColor: "#000",
                titleColor: 'white',
                titleColorActive: 'black',
                datePadding: "100px",
            }}
            disableToolbar
            buttonTexts={{
                first: 'Jump to First',
                last: 'Jump to Last',
                next: 'Next',
                previous: 'Previous',
              }}
        />
    </div>
  )
}

const Timeline = () => {

  return (
    <div style={{ backgroundColor: '#64a9ea'}}>
        <div style={{ padding: '0 100px' }}>
            <section className="project" id="projects" >

            <Container>
                <Row>
                <Col>

                        <h2>Projet</h2>
                        <p style={{ color: 'white' }}>
                            <b>
                            Tidaly est une application web et mobile pour surveiller votre consommation d'eau en temps réel.

                            Suivez votre consommation sur chaque point d'eau, chez vous ou bien en déplacement.

                            Avec l'analyse de données, les alertes et le coaching personnalisé, économisez de l'eau et de l'argent en
                            comprenant votre consommation.
                            <hr style={{ border:'3px solid #bbb;' }}></hr>
                            </b>
                        </p>

                        <Home></Home>

                </Col>
                </Row>
            </Container>
            </section>
        </div>
    </div>

  );
};

export default Timeline;
