import React, { useEffect } from 'react';
import Hammer from 'hammerjs';
import logoTidaly from "../assets/img/logoTidaly.png"
import rightArrow from "../assets/img/right.png"
import leftArrow from "../assets/img/left.png"
import { Container, Row, Col, Tab, Nav } from 'react-bootstrap';

const Timeline = () => {
  useEffect(() => {
    const timeline = document.querySelector('.timeline ol');
    const elH = document.querySelectorAll('.timeline li > div');
    const arrows = document.querySelectorAll('.timeline .arrows .arrow');
    const arrowPrev = document.querySelector('.timeline .arrows .arrow__prev');
    const arrowNext = document.querySelector('.timeline .arrows .arrow__next');
    const firstItem = document.querySelector('.timeline li:first-child');
    const lastItem = document.querySelector('.timeline li:last-child');
    const xScrolling = 280;
    const disabledClass = 'disabled';

    const init = () => {
      setEqualHeights(elH);
      animateTl(xScrolling, arrows, timeline);
      setSwipeFn(timeline, arrowPrev, arrowNext);
      setKeyboardFn(arrowPrev, arrowNext);
    };

    const setEqualHeights = (el) => {
      let counter = 0;
      for (let i = 0; i < el.length; i++) {
        const singleHeight = el[i].offsetHeight;

        if (counter < singleHeight) {
          counter = singleHeight;
        }
      }

      for (let i = 0; i < el.length; i++) {
        el[i].style.height = `${counter}px`;
      }
    };

    const isElementInViewport = (el) => {
      const rect = el.getBoundingClientRect();
      return (
        rect.top >= 0 &&
        rect.left >= 0 &&
        rect.bottom <=
          (window.innerHeight || document.documentElement.clientHeight) &&
        rect.right <= (window.innerWidth || document.documentElement.clientWidth)
      );
    };

    const setBtnState = (el, flag = true) => {
      if (flag) {
        el.classList.add(disabledClass);
      } else {
        if (el.classList.contains(disabledClass)) {
          el.classList.remove(disabledClass);
        }
        el.disabled = false;
      }
    };

    const animateTl = (scrolling, el, tl) => {
      let counter = 0;
      for (let i = 0; i < el.length; i++) {
        el[i].addEventListener('click', function () {
          const sign = this.classList.contains('arrow__prev') ? '' : '-';
          if (counter === 0 && sign === '-') {
            // If the timeline is at the beginning and right arrow is clicked,
            // don't disable the left arrow
          } else {
            arrowPrev.disabled = true; // Disable left arrow initially
          }

          if (counter === 0) {
            tl.style.transform = `translateX(-${scrolling}px)`;
          } else {
            const tlStyle = getComputedStyle(tl);
            const tlTransform =
              tlStyle.getPropertyValue('-webkit-transform') ||
              tlStyle.getPropertyValue('transform');
            const values =
              parseInt(tlTransform.split(',')[4]) +
              parseInt(`${sign}${scrolling}`);
            tl.style.transform = `translateX(${values}px)`;
          }

          setTimeout(() => {
            isElementInViewport(firstItem)
              ? setBtnState(arrowPrev)
              : setBtnState(arrowPrev, false);
            isElementInViewport(lastItem)
              ? setBtnState(arrowNext)
              : setBtnState(arrowNext, false);
          }, 1100);

          counter++;
        });
      }
    };

    const setSwipeFn = (tl, prev, next) => {
      const hammer = new Hammer(tl);
      hammer.on('swipeleft', () => next.click());
      hammer.on('swiperight', () => prev.click());
    };

    const setKeyboardFn = (prev, next) => {
      document.addEventListener('keydown', (e) => {
        if (e.which === 37 || e.which === 39) {
          const timelineOfTop = timeline.offsetTop;
          const y = window.pageYOffset;
          if (timelineOfTop !== y) {
            window.scrollTo(0, timelineOfTop);
          }
          if (e.which === 37) {
            prev.click();
          } else if (e.which === 39) {
            next.click();
          }
        }
      });
    };

    init();

    return () => {
      // Cleanup if needed
    };
  }, []);

  return (
    <div>
        <section className="project" id="projects" style={{ marginBottom: '-90px', marginTop: '-180px'}}>
            <Container>
            <Row>
            <Col>


                    <div>
                    <h2>Projet</h2>
                    <p style={{ color: 'white' }}>
                        <b>
                        Tidaly est une application web et mobile pour surveiller votre consommation d'eau en temps réel.

                        Suivez votre consommation sur chaque point d'eau, chez vous ou bien en déplacement.

                        Avec l'analyse de données, les alertes et le coaching personnalisé, économisez de l'eau et de l'argent en
                        comprenant votre consommation.
                        <hr style={{ border:'3px solid #bbb;' }}></hr>
                        </b>
                    </p>
                    </div>

            </Col>
            </Row>
        </Container>
        </section>

        <section className="timeline">
        <ol>
            <li>
            <div>
                <time>1934  <img src={logoTidaly} alt="Logo" style={{ width: '15px', height: 'auto', marginLeft: '5px', marginTop: '-5px' }} /></time> At vero eos et accusamus et iusto odio dignissimos ducimus qui blanditiis praesentium At vero eos et accusamus et iusto odio dignissimos ducimus qui blanditiis praesentium
            </div>
            </li>
            <li>
            <div>
                <time>1937</time> Proin quam velit, efficitur vel neque vitae, rhoncus commodo mi. Suspendisse finibus mauris et bibendum molestie. Aenean ex augue, varius et pulvinar in, pretium non nisi.
            </div>
            </li>
            <li>
            <div>
                <time>1940</time> Proin iaculis, nibh eget efficitur varius, libero tellus porta dolor, at pulvinar tortor ex eget ligula. Integer eu dapibus arcu, sit amet sollicitudin eros.
            </div>
            </li>
            <li>
            <div>
                <time>1943</time> In mattis elit vitae odio posuere, nec maximus massa varius. Suspendisse varius volutpat mattis. Vestibulum id magna est.
            </div>
            </li>
            <li>
            <div>
                <time>1946</time> In mattis elit vitae odio posuere, nec maximus massa varius. Suspendisse varius volutpat mattis. Vestibulum id magna est.
            </div>
            </li>
            <li>
            <div>
                <time>1956</time> In mattis elit vitae odio posuere, nec maximus massa varius. Suspendisse varius volutpat mattis. Vestibulum id magna est.
            </div>
            </li>
            <li>
            <div>
                <time>1957</time> In mattis elit vitae odio posuere, nec maximus massa varius. Suspendisse varius volutpat mattis. Vestibulum id magna est.
            </div>
            </li>
            <li>
            <div>
                <time>1967</time> Aenean condimentum odio a bibendum rhoncus. Ut mauris felis, volutpat eget porta faucibus, euismod quis ante.
            </div>
            </li>
