import React, { useState, useEffect, useMemo } from 'react';
import meter2 from "../assets/img/team/Nathan-Team22.png";
import meter1 from "../assets/img/team/Philippe-Team.png";
import meter3 from "../assets/img/team/Killian-Team.png";
import meter4 from "../assets/img/team/Maxime-Team.png";
import meter5 from "../assets/img/team/Isaak-Team.png";
import meter6 from "../assets/img/team/Clement-Team.png";
import meter7 from "../assets/img/team/Quentin-Team.png";
import Carousel from 'react-multi-carousel';
import 'react-multi-carousel/lib/styles.css';





export const Skills = () => {

  const responsive = {
    superLargeDesktop: {
      // the naming can be any, depends on you.
      breakpoint: { max: 4000, min: 3000 },
      items: 5
    },
    desktop: {
      breakpoint: { max: 3000, min: 1024 },
      items: 3
    },
    tablet: {
      breakpoint: { max: 1024, min: 464 },
      items: 1
    },
    mobile: {
      breakpoint: { max: 464, min: 0 },
      items: 1
    }
  };

  const CustomDot = ({ onClick, index, active }) => {
    return (

      <button
        className={`react-multi-carousel-dot-list ${active ? "active" : "inactive"}`}
        onClick={() => onClick()}
      >
      </button>
    );
  };


   const MemoizedCarousel = React.memo(({ images }) => (
    <Carousel responsive={responsive} className="skill-slider" infinite={true} showDots={true} CustomDot={CustomDot}>

              <div className="item">
                  <img src={meter1} alt="Image" />
                </div>
                <div className="item">
                  <img src={meter2} alt="Image" />
                </div>
                <div className="item">
                  <img src={meter3} alt="Image" />
                </div>
                <div className="item">
                  <img src={meter4} alt="Image" />
                </div>
                <div className="item">
                  <img src={meter5} alt="Image" />
                </div>
                <div className="item">
                  <img src={meter6} alt="Image" />
                </div>
                <div className="item">
                  <img src={meter7} alt="Image" />
                  <h5 style={{ color: "white"}}>fa</h5>
                  <h5 style={{ color: "white"}}>dz</h5>
                </div>
    </Carousel>
  ));


  return (
    <section className="skill" id="team">
      <div className="container">
        <div className="row">
          <div className="col-12">
            <div className="skill-bx wow zoomIn">
              <h2>Equipe</h2>
              {/* <h3 style={{ color: "#64a9ea" }}>{renderNameTeamWorker(slideIndex)}</h3>

              <p>{renderPositionTeamWorker(slideIndex)}</p> */}

              <MemoizedCarousel images={meterImages} />

              {/* <p style={{ color: "black", margin: 0, padding: 0 }}>{(slideIndex + 1)+ "/7"}</p> */}
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};
